import React from "react";

const Blog = () => {
  const blogPosts = [
    {
      title: "How I Built My Portfolio",
      date: "November 22, 2024",
      content:
        "A detailed walkthrough of the tools, technologies, and decisions I used to build this portfolio. From setting up React to styling with Tailwind CSS.",
    },
    {
      title: "My Web Development Journey",
      date: "November 15, 2024",
      content:
        "A brief story of how I became a web developer, the challenges I faced, and the resources I found most helpful along the way.",
    },
    {
      title: "Learning React: My Experience",
      date: "October 30, 2024",
      content:
        "React has been a game changer in the way I build user interfaces. In this post, I share my experience learning React and some tips for beginners.",
    },
  ];

  return (
    <section id="blog" className="bg-gray-100 py-16 px-8">
      <h2 className="text-4xl font-bold text-center text-black mb-8">Blog</h2>
      <div className="space-y-8">
        {blogPosts.map((post, index) => (
          <div
            key={index}
            className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-all duration-300"
          >
            <h3 className="text-2xl font-semibold text-black">{post.title}</h3>
            <p className="text-gray-500 text-sm mb-4">{post.date}</p>
            <p className="text-lg text-gray-800">{post.content}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Blog;
