import React from "react";

const Projects = () => {
  const projects = [
    {
      id: 1,
      name: "Project One",
      description: "A brief description of your project goes here.",
      image: "C:\Users\imadm\my-portfolio\src\assets\back.png", // Replace with the actual path to your image
    },
    {
      id: 2,
      name: "Project Two",
      description: "Another exciting project description goes here.",
      image: "path/to/project2.jpg", // Replace with the actual path to your image
    },
    {
      id: 3,
      name: "Project Three",
      description: "Describe what makes this project unique.",
      image: "path/to/project3.jpg", // Replace with the actual path to your image
    },
    {
      id: 4,
      name: "Project Four",
      description: "Showcase your work with an engaging description.",
      image: "path/to/project4.jpg", // Replace with the actual path to your image
    },
    {
      id: 5,
      name: "Project Five",
      description: "Another project to display your skills.",
      image: "path/to/project5.jpg", // Replace with the actual path to your image
    },
    {
      id: 6,
      name: "Project Six",
      description: "Brief but impactful summary of the project.",
      image: "path/to/project6.jpg", // Replace with the actual path to your image
    },
  ];

  return (
    <section id="projects" className="bg-black text-white py-16">
      <div className="container mx-auto">
        <h2 className="text-3xl font-bold mb-8">My Projects</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {projects.map((project) => (
            <div
              key={project.id}
              className="bg-gray-800 p-4 rounded-md shadow-md hover:shadow-lg transition-shadow duration-300"
            >
              <img
                src={project.image}
                alt={project.name}
                className="w-full h-40 object-cover rounded-t-md"
              />
              <div className="p-4">
                <h3 className="text-xl font-semibold">{project.name}</h3>
                <p className="mt-2 text-gray-300">{project.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
