const About = () => {
    return (
      <section
        id="about"
        className="py-20 bg-gradient-to-r from-gray-100 via-gray-200 to-gray-300 text-gray-800"
      >
        <div className="container mx-auto text-center">
          <h2 className="text-4xl font-bold mb-6 text-gray-700">About Me</h2>
          <p className="text-lg md:text-xl">
            I am a passionate developer with expertise in building high-quality
            web applications.
          </p>
        </div>
      </section>
    );
  };
  
  export default About;
