import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";

const AnimatedNumber = ({ endValue }) => {
  const [number, setNumber] = useState(0);

  useEffect(() => {
    let counter = 0;
    const interval = setInterval(() => {
      if (counter < endValue) {
        counter += 1;
        setNumber(counter);
      } else {
        clearInterval(interval);
      }
    }, 100); // Adjust speed of the animation (50ms per step)
    return () => clearInterval(interval);
  }, [endValue]);

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1 }}
      className="text-4xl font-bold"
    >
      {`+${number}`} {/* Display the number with a + symbol */}
    </motion.div>
  );
};

const AnimatedStats = () => {
  return (
    <div className="flex justify-around items-center">
      <div className="flex flex-col items-center">
        <h2 className="text-white text-4xl font-bold">Projects</h2>
        <AnimatedNumber endValue={10} /> {/* Replace with dynamic number */}
      </div>
      <div className="flex flex-col items-center">
        <h2 className="text-white text-4xl font-bold">Years</h2>
        <AnimatedNumber endValue={2} /> {/* Replace with dynamic number */}
      </div>
    </div>
  );
};

export default AnimatedStats;
