const Skills = () => {
    const skillCategories = [
      {
        title: "Frontend Development",
        skills: ["HTML", "CSS", "JavaScript", "React", "Tailwind CSS"],
      },
      {
        title: "Backend Development",
        skills: ["Node.js", "Express", "MongoDB", "REST APIs"],
      },
      {
        title: "Tools & Platforms",
        skills: ["Git", "GitHub", "Postman", "Vercel", "Netlify"],
      },
    ];
  
    return (
      <section id="skills" className="py-20 bg-gray-50 text-gray-800">
        <div className="container mx-auto">
          <h2 className="text-4xl font-bold text-center mb-10 text-gray-700">
            My Skills
          </h2>
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-10">
            {skillCategories.map((category, index) => (
              <div
                key={index}
                className="bg-white p-6 rounded-lg shadow-lg border border-gray-200"
              >
                <h3 className="text-2xl font-semibold mb-4 text-gray-700">
                  {category.title}
                </h3>
                <ul className="flex flex-wrap gap-3">
                  {category.skills.map((skill, idx) => (
                    <li
                      key={idx}
                      className="bg-blue-100 text-blue-600 px-4 py-2 rounded-full text-sm font-medium"
                    >
                      {skill}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
      </section>
    );
  };
  
  export default Skills;
